"use strict";


var db = require('pg');
//or native libpq bindings
//var pg = require('pg').native

var kwarque = require('kwarque');
var quadtree = require('quadtree');

var tree = quadtree.init({
    x : -20037508.34,
    y : -20037508.34,
    w : 20037508.34 * 2,
    h : 20037508.34 * 2,
    maxDepth : 16,
    maxChildren : 128
});

var config = {
    host : '/var/run/postgresql',
    database : 'kwarque'
};

var queries = {
    'getFragments'   : "SELECT id, location[0] AS lon, location[1] AS lat, title, text FROM fragment WHERE location <@ BOX(POINT($1, $2), POINT($3, $4))",
    'insertFragment' : "INSERT INTO fragment (location, title, text) VALUES (POINT($1, $2), $3, $4) RETURNING id"
}

var query = function(name, values, row, end) {
    db.connect(config, function(err, client) {
        if (err) {
            if (end) end("error");
        } else {
            var q = client.query({
                name : name,
                text : queries[name],
                values : values
            });
            if (row) q.on("row", row);
            if (end) {
                q.on("end", function() {
                    end('end', end.id);
                });
                q.on("error", function(message) {
                    console.log(message);
                    end("error");
                });
            }
        }
    });
};

var getFragments = function(select, row, end) {
    query('getFragments', [
        select.x,
        select.y,
        select.x + select.w,
        select.y + select.h,
    ], row, end);
};
        
var insertFragment = function(data, end) {
    query('insertFragment', [data.lon, data.lat, data.title, data.text], function(row) {
        end.id = row.id;
    }, end);
};

kwarque.on('clientConnect', function(client) {
    client.watchedNodes = [];
    /**
     * watch an area on the map.
     */
    client.on("watch", function(select, fn) {
        getFragments(select, function(row) {
            client.emit('fragment', row);
        }, fn);
        client.join('#' + select.depth + '_' + select.x + '_' + select.y);
    });
    /**
     * ignore an area on the map
     */
    client.on("ignore", function(fn) {
        for (var i = 0; i < client.watchedNodes.length - 1; ++i) {
            var node = client.watchedNodes[i];
            client.leave('#' + node.depth + '_' + node.x + '_' + node.y);
        }
        fn('end');
    });
            
    client.on("insert", function(data, fn) {
        var handler = function(result) {
            fn(result, handler.id);
            tree.retrieveNodes({
                x : data.x,
                y : data.y,
                w : 0, 
                h : 0
            }, function(node) {
                client.broadcast.to('#' + node.depth + '_' + node.x + '_' + node.y).emit('fragment', {
                    id : handler.id,
                    x : data.x,
                    y : data.y,
                    title : data.title,
                    text : data.text
                });
            });
        };
        insertFragment(data, handler);
    });
});